import stockList from './stocksymbols.json';
import store from './store/index.js';
import { fetchAllHist, fetchAllInfo, getQuotes } from './util/api-calls';
// Load up components
import Portfolio from './components/portfolio';
import StockTicker from './components/stockticker';

export const stocks = [{
  sym: 'TSLA',
  shares: 10,
  name: 'Tesla Motors, Inc.'
}, {
  sym: 'GOOG',
  shares: 4,
  name: 'Google Inc.'
}, {
  sym: 'MSFT',
  shares: 24,
  name: 'Microsoft Corporation'
}, {
  sym: 'AAPL',
  shares: 9,
  name: 'APPLE Inc.'
}, {
  sym: 'AMZN',
  shares: 3,
  name: 'Amazon.com Inc.'
}];

document.addEventListener('DOMContentLoaded', e => {
  // DEFALT SETTINGS
  store.dispatch('fetchAllStockSymbols', stockList);
  fetchAllHist(stocks).then(history => {
    store.dispatch('fetchAllhistory', history);
  }).then(() => {
    fetchAllInfo(stocks).then(info => {
      store.dispatch('fetchAllinfo', info);
    }).then(() => {
      getQuotes(stocks).then(data => {
        store.dispatch('updateTicker', data);
        const porfolio = new Portfolio();
        const ticker = new StockTicker();
        porfolio.render();
        ticker.render();
        ticker.updateTicker();
      });
    });
  });
});

import actions from './actions.js';
import mutations from './mutations.js';
import state from './state.js';
import Store from './store.js';

export default new Store({
  actions,
  mutations,
  state
});

/* eslint-disable require-jsdoc */
const apikeys = require('./apikey');

export async function fetchAllHist(syms) {
  try {
    const history = await Promise.all(syms.map(async sym => await (await fetch(`https://cloud.iexapis.com/stable/stock/${sym.sym}/chart/1y?token=${apikeys.iexkey}&chartInterval=24`)).json()));
    const result = {};
    syms.forEach((sym, i) => {
      result[sym.sym] = history[i];
    });
    return result;
  } catch (err) {
    console.log(err);
  }
};

export async function fetchAllInfo(syms) {
  try {
    const info = await Promise.all(syms.map(async sym => await (await fetch(`https://cloud.iexapis.com/stable/stock/${sym.sym}/company?token=${apikeys.iexkey}`)).json()));
    const res = {};
    syms.forEach((sym, i) => {
      res[sym.sym] = {
        shares: sym.shares,
        info: info[i]
      };
    });
    return res;
