export async function getQuotes(syms) {
  try {
    const info = await Promise.all(syms.map(async sym => await (await fetch(`https://cloud.iexapis.com/stable/stock/${sym.sym}/quote?token=${apikeys.iexkey}`)).json()));
    const res = [];
    info.forEach(item => {
      res.push({
        symbol: item.symbol,
        price: item.latestPrice,
        diff: item.change
      });
    });
    return res;
  } catch (err) {
    console.log(err);
  }
}
