import Component from '../lib/component.js';
import store from '../store/index.js';
import { getQuotes } from '../util/api-calls';
import { stocks } from '../index';
export default class StockTicker extends Component {
  constructor() {
    super({
      store: store,
      element: document.querySelectorAll('.price')
    });
  }
  updateTicker() {
    setInterval(e => {
      getQuotes(stocks).then(data => {
        store.dispatch('updateTicker', data);
      });
    }, 9000);
  }
  render() {
    const { ticker } = store.state;
    ticker.forEach((stock, i) => {
      const observer = new MutationObserver(() => {
        this.element[i].classList.add('changed');
        setTimeout(() => {
          this.element[i].classList.remove('changed');
        }, 1500);
      });
      observer.observe(this.element[i], { childList: true });
      const content = `
        <span class=${stock.diff > 0 ? '"green"' : '"red"'}>
          <span class="price-item">${stock.symbol}</span>
          <span class="price-item">${stock.price}</span>
          <span class="price-item">${stock.diff}</span>
        </span>`;
      if (this.element[i].innerHTML !== content) {
        this.element[i].innerHTML = content;
      }
    });
  }
};

export default {
  fetchAllStockSymbols(context, payload) {
    context.commit('fetchAllStockSymbols', payload);
  },
  fetchAllinfo(context, payload) {
    context.commit('fetchAllinfo', payload);
  },
  fetchAllhistory(context, payload) {
    context.commit('fetchAllhistory', payload);
  },
  addShare(context, payload) {
    context.commit('addShare', payload);
  },
  updateShares(context, payload) {
    context.commit('updateShare', payload);
  },
  minusShare(context, payload) {
    context.commit('minusShare', payload);
  },
  closeModal(context) {
    context.commit('closeModal');
  },
  updateTicker(context, payload) {
    context.commit('updateTicker', payload);
  }
};

export default {
  fetchAllStockSymbols(state, payload) {
    state.symbols = payload;
    return state;
  },
  fetchAllinfo(state, payload) {
    state.company = payload;
    return state;
  },
  fetchAllhistory(state, payload) {
    state.history = payload;
    return state;
  },
  addShare(state, payload) {
    state.company[payload].shares += 1;
    return state;
  },
  minusShare(state, payload) {
    if (state.company[payload].shares > 0) {
      state.company[payload].shares -= 1;
    }
    return state;
  },
  updateShare(state, payload) {
    if (state.company[payload.sym].shares > 0) {
      state.company[payload.sym].shares = payload.num;
    }
  },
  closeModal(state) {
    state.modal = false;
    return state;
  },
  updateTicker(state, payload) {
    state.ticker = payload;
    return state;
  }
};

export default {
  symbols: {},
  company: {},
  history: {},
  modal: true,
  ticker: []
};

